import React, { useState, useEffect } from "react";
import styled from "styled-components";

const MessageTextArea = styled.textarea`
  display: block;
  margin-top: 8px;
  width: 95%;
  border-color: rgba(0, 0, 0, 0.125);
`;

const API_ENDPOINT =
  "https://student-json-api.lidemy.me/comments?_sort=createdAt&_order=desc";

const Body: React.FC = () => {

  const [messages, setMessages] = useState([]);
  const [apiError, setApiError] = useState(null);

  // 第二個參數傳入 [] 代表只在 componet mount 後執行
  useEffect(() => {
    fetch(API_ENDPOINT)
      .then((res) => res.json())
      .then((data) => {
        setMessages(data);
      })
      .catch((err) => {
        setApiError(err.message);
      });
  }, []);

  function counter() {
    return <h1>hello</h1>;
  }
  return (
    <>
      <div>
        <button className="btn btn-info" onClick={counter}>
          Add Review
        </button>
      </div>
      <div className="col">
        <input
          id="searchName"
          className="searchName"
          type="text"
          placeholder="Search..."
        />
        <span className="sortWays">Sort by time | Sort by rate</span>
        <table>
          <tr>
            <td>Rest1</td>
            <td>Rating1</td>
          </tr>
          <tr>
            <td>Rest2</td>
            <td>Rating2</td>
          </tr>
          <tr>
            <td>Rest3</td>
            <td>Rating3</td>
          </tr>
          <tr>
            <td>Rest4</td>
            <td>Rating4</td>
          </tr>
        </table>
      </div>
    </>
  );
};

const App: React.FC = () => {
  return (
    <div className="body">
       <MessageTextArea rows={8} />
      <Body />
    </div>
  );
};

export default App;
